jQuery(document).ready(function (n) {
    n(".custom_move").on("click", function (t) {
        n(".cover").toggleClass("on");
    }), n(".custom_play,.custom_stop").on("click", function (t) {
        n(".custom_play,.custom_stop").toggleClass("hide");
    }), n(".custom_move").on("click", function (t) {
        n(this).toggleClass("active");
    });
    var t = 0,
        o = 0,
        e = !1;
    n(document).on("mousemove", function (i) {
        !0 === e && (n(document).scrollTop(parseInt(n(document).scrollTop() + (t - i.pageY))),
            n(document).scrollLeft(parseInt(n(document).scrollLeft() + (o - i.pageX))));
    }), n(".cover").on("mousedown", function (n) {
        e = !0, t = n.pageY, o = n.pageX, n.preventDefault();
    }), n(window).on("mouseup", function (n) {
        e = !1;
    }), n(window).on("mouseout", function (n) {
        e = !1;
    }),
        n(document).on("touchmove", function (i) {
            !0 === e && (n(document).scrollTop(parseInt(n(document).scrollTop() + (t - i.touches[0].pageY))),
                n(document).scrollLeft(parseInt(n(document).scrollLeft()
                    + (o - i.touches[0].pageX))));
        }), n(".cover").on("touchstart", function (n) {
            e = !0,
            t = n.touches[0].pageY,
            o = n.touches[0].pageX,
            n.preventDefault();
        });
});